function OUTPUT=sincint0_mb(INPUT,Lx)% Lx-fold zooming of a 1-D signal with sinc-interpolation;% (round(SzX/2)+1)th sample of the mask is equal to 0% NO SIGNAL EXTENSION% Call OUTPUT=sincint0(INPUT,Lx);SzX =max(size(INPUT));INPUT=reshape(INPUT,SzX,1);%OUTPUT=zeros(Lx*SzX,1);Ux=1/(Lx);M=zeros(SzX,1);SzX_half=round(SzX/2);M(1)=1; M(SzX_half+1)=0;r=2:SzX_half; Wn=1i*2*pi*Ux/SzX;M(r)=exp(Wn*(r-1)); M(SzX+2-r)=conj(M(r));spinput=fftnorm_mb(INPUT);spinput_2 =  (spinput*ones(1,Lx)).*(cumprod(M*ones(1, Lx), 2));out_2 = real(ifft(spinput_2))';OUTPUT = out_2(:);% % for l=1:Lx,%         spinput=spinput.*M;%         out=real(ifftnorm_mb(spinput));%         key=zeros(Lx,1);key(l)=1;%         OUTPUT=kron(out,key)+OUTPUT;% %         plotnorm_mb(OUTPUT,1,1,1);% %         title(['sincint0.m: Interpolated signal, Lint=',num2str(Lx)]);% %         drawnow;% end