function OUT=lchsttst(INPIMG,LX,LY)% Histogram of an image fragment selected % interactively by the user% Call  OUT=lchsttst(INPIMG,LX,LY);INPIMG = double(INPIMG);clfcolormap(gray(256));h_g=imghisto(INPIMG);s='y';while s=='y',frgm=fragment(INPIMG,LX,LY);OUT=imghisto(frgm);subplot(221);display1(INPIMG);drawnowplotnorm(h_g,2,2,3);title('Image global histogram');drawnowsubplot(222); display1(frgm);title('Selected fragment');plotnorm(OUT,2,2,4)title('Histogram of the fragment');s=input('Do you want to continue? (y or n)  ','s'); end