function OUTIMG=target_mb(INPIMG,x,y, k)x = round(x);y = round(y);% This program forms a target image for correlation % from INPIMG by using target masks tgmask32, tgmask48, tgmask64 or tgmask96% The target is centered at (x,y) coordinates in INPIMG % and placed in the center of OUTIMG;% Coordinates (x,y) are supposed to be obtained manually by using ginput.m% Call OUTIMG=target_(INPIMG,x,y);[SzX,SzY]=size(INPIMG);LX=SzX/2;LY=SzY/2;IMG=zeros(2*SzX,2*SzY);IMG(SzX/2+1:1.5*SzX,SzY/2+1:1.5*SzY)=INPIMG;if      k==1 mask=zeros(8);mask(4:6,4:6)=ones(3);elseif 	k==2 load tgmask32;mask=mask32(1:4:32,1:4:32);elseif 	k==3 load tgmask32;mask=mask32(1:2:32,1:2:32);elseif  k==4 load tgmask32;mask=mask32;elseif 	k==5 load tgmask48;mask=mask48;elseif 	k==6 load tgmask64;mask=mask64;else   	     load tgmask96;mask=mask96; end[szX szY]=size(mask); Lx=szX/2; Ly=szY/2;trgt=IMG(SzX/2+x-Lx+2:SzX/2+x+Lx+1,SzY/2+y-Ly+2:SzY/2+y+Ly+1).*mask;% OUTIMG=zeros(SzX,SzY);% OUTIMG(LX-Lx+2:LX+Lx+1,LY-Ly+2:LY+Ly+1)=trgt;OUTIMG = trgt;